mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY BRD_MSTR_INFO_SEQ DESC) AS RNO , BRD_MSTR_INFO_SEQ , BRD_TYPE_CD , SUBJECT_TYPE_CD ,
, TITLE , CONTENT , NOTICE_YN , (SELECT COUNT(*) FROM BRD_CMNT_INFO WHERE DEL_YN = 'N' AND BRD_MSTR_INFO_SEQ = A.BRD_MSTR_INFO_SEQ) AS CMNT_CNT , (SELECT SUM(CASE WHEN LIKE_YN = 'Y' THEN 1 WHEN LIKE_YN = 'N' THEN -1 END) FROM BRD_LIKES WHERE BRD_MSTR_INFO_SEQ = A.BRD_MSTR_INFO_SEQ) AS LIKE_TOTAL_CNT , (SELECT COUNT(*) FROM BRD_VIEWS WHERE DEL_YN = 'N' AND BRD_MSTR_INFO_SEQ = A.BRD_MSTR_INFO_SEQ) AS VIEW_CNT , REGIST_ID , REGIST_NM , DATE_FORMAT(REG_TS, 'yyyy-MM-dd') AS REG_DT , DATE_FORMAT(REG_TS, 'yyyy-MM-dd hh:mm:ss') AS REG_TS FROM BRD_MSTR_INFO A WHERE 1 = 1
LIMIT #{pageSize} OFFSET #{offset}

SELECT BRD_MSTR_INFO_SEQ , BRD_TYPE_CD ,
, SUBJECT_TYPE_CD ,
, TITLE , CONTENT , NOTICE_YN , (SELECT COUNT(*) FROM BRD_CMNT_INFO WHERE DEL_YN = 'N' AND BRD_MSTR_INFO_SEQ = A.BRD_MSTR_INFO_SEQ) AS CMNT_CNT , (SELECT SUM(CASE WHEN LIKE_YN = 'Y' THEN 1 WHEN LIKE_YN = 'N' THEN -1 END) FROM BRD_LIKES WHERE BRD_MSTR_INFO_SEQ = A.BRD_MSTR_INFO_SEQ) AS LIKE_TOTAL_CNT , (SELECT COUNT(*) FROM BRD_VIEWS WHERE DEL_YN = 'N' AND BRD_MSTR_INFO_SEQ = A.BRD_MSTR_INFO_SEQ) AS VIEW_CNT , REGIST_ID , REGIST_NM , DATE_FORMAT(REG_TS, 'yyyy-MM-dd') AS REG_DT , DATE_FORMAT(REG_TS, 'yyyy-MM-dd hh:mm:ss') AS REG_TS FROM BRD_MSTR_INFO A WHERE BRD_MSTR_INFO_SEQ = #{brdMstrInfoSeq}

mapper.insert:

	SELECT IFNULL(MAX(BRD_MSTR_INFO_SEQ), 0) + 1 FROM BRD_MSTR_INFO

INSERT INTO BRD_MSTR_INFO (
	BRD_MSTR_INFO_SEQ
	, BRD_TYPE_CD
	, SUBJECT_TYPE_CD
	, TITLE
	, CONTENT
	, NOTICE_YN
	, DEL_YN
	, REGIST_ID
	, REGIST_NM
	, REG_TS
	, MDFY_TS
) VALUES (
	#{brdMstrInfoSeq}
	, #{brdTypeCd}
	, #{subjectTypeCd}
	, #{title}
	, #{content}
	, #{noticeYn}
	, 'N'
	, #{registId}
	, #{registNm}
	, CURRENT_TIMESTAMP
	, CURRENT_TIMESTAMP
)

mapper.update:
UPDATE BRD_MSTR_INFO SET
	BRD_TYPE_CD = #{brdTypeCd}
	, SUBJECT_TYPE_CD = #{subjectTypeCd}
	, TITLE = #{title}
	, CONTENT = #{content}
	, NOTICE_YN = #{noticeYn}
	, MDFY_TS = CURRENT_TIMESTAMP
WHERE BRD_MSTR_INFO_SEQ = #{brdMstrInfoSeq}
