mapper.select:
SELECT COUNT(*)
FROM BRD_VIEWS
WHERE BRD_MSTR_INFO_SEQ = #{brdMstrInfoSeq}
  AND REGIST_ID = #{registId}

mapper.insert:

	SELECT IFNULL(MAX(BRD_VIEWS_SEQ), 0) + 1 FROM BRD_VIEWS

INSERT INTO BRD_VIEWS (
	BRD_VIEWS_SEQ
	, BRD_MSTR_INFO_SEQ
	, REGIST_ID
	, REGIST_NM
	, REG_TS
) VALUES (
	#{brdViewsSeq}
	, #{brdMstrInfoSeq}
	, #{registId}
	, #{registNm}
	, CURRENT_TIMESTAMP
)
