mapper.insert:

	SELECT IFNULL(MAX(QUIZ_MSTR_DTL_SEQ), 0) + 1 FROM QUIZ_MSTR_DTL

INSERT INTO QUIZ_DTL_INFO (
	QUIZ_DTL_INFO_SEQ
	, QUIZ_MSTR_INFO_SEQ
	, SRT_NO
	, CONTENT
	, CMNTR
	, DEL_YN
	, REGIST_ID
	, REGIST_NM
	, REG_TS
	, MDFY_TS
) VALUES (
	#{quizDtlInfoSeq}
	, #{quizMstrInfoSeq}
	, #{srtNo}
	, #{content}
	, #{cmntr}
	, 'N'
	, #{registId}
	, #{registNm}
	, CURRENT_TIMESTAMP
	, CURRENT_TIMESTAMP
)

mapper.update:
UPDATE QUIZ_DTL_INFO SET
	CONTENT = #{content}
   , CMNTR = #{cmntr}
   , MDFY_TS = CURRENT_TIMESTAMP
WHERE QUIZ_DTL_INFO_SEQ = #{quizDtlInfoSeq}
