mapper.select:
SELECT ROW_NUMBER() OVER(PARTITION BY A.SUBJECT_TYPE_CD ORDER BY A.SUBJECT_TYPE_CD, A.SRT_NO) AS RNO , A.QUIZ_MSTR_INFO_SEQ , B.QUIZ_USER_ANS_SEQ , A.SUBJECT_TYPE_CD , A.SRT_NO , B.USER_ANSWER FROM QUIZ_MSTR_INFO A LEFT JOIN (SELECT * FROM QUIZ_USER_ANS WHERE USER_ID = #{userId}) B ON A.QUIZ_MSTR_INFO_SEQ = B.QUIZ_MSTR_INFO_SEQ WHERE 1 = 1 AND A.DEL_YN = 'N' AND A.EXAM_GRP_CD = #{examGrpCd} AND A.EXAM_YEAR = #{examYear} AND A.EXAM_NO = #{examNo}
AND A.SUBJECT_TYPE_CD = #{subjectTypeCd}
ORDER BY A.SUBJECT_TYPE_CD, A.SRT_NO

SELECT COUNT(*)
FROM QUIZ_MSTR_INFO A,
     QUIZ_USER_ANS B
WHERE 1 = 1
  AND A.QUIZ_MSTR_INFO_SEQ = B.QUIZ_MSTR_INFO_SEQ
  AND A.DEL_YN = 'N'
  AND A.EXAM_GRP_CD = #{examGrpCd}
  AND A.EXAM_YEAR = #{examYear}
  AND A.EXAM_NO = #{examNo}
  AND A.SUBJECT_TYPE_CD = #{subjectTypeCd}
  AND B.USER_ID = #{userId}
  AND (B.USER_ANSWER IS NULL OR B.USER_ANSWER != '')

SELECT
ROW_NUMBER() OVER(ORDER BY SUBJECT_TYPE_CD, SRT_NO) AS rno
, QUIZ_MSTR_INFO_SEQ AS quizMstrInfoSeq
, SUBJECT_TYPE_CD AS subjectTypeCd
, SRT_NO AS srtNo
FROM QUIZ_MSTR_INFO
WHERE DEL_YN = 'N'

    AND EXAM_YEAR = ${examYear}


    AND EXAM_GRP_CD = ${examGrpCd}


    AND EXAM_NO = ${examNo}


    AND SUBJECT_TYPE_CD = ${subjectTypeCd}

mapper.insert:

    SELECT IFNULL(MAX(QUIZ_MSTR_INFO_SEQ), 0) + 1 FROM QUIZ_MSTR_INFO

INSERT INTO QUIZ_MSTR_INFO (
QUIZ_MSTR_INFO_SEQ
, EXAM_YEAR
, EXAM_GRP_CD
, EXAM_NO
, SUBJECT_TYPE_CD
, SRT_NO
, CONTENT
, ANSWER
, CMNTR
, DEL_YN
, REGIST_ID
, REGIST_NM
, REG_TS
, MDFY_TS
) VALUES (
#{quizMstrInfoSeq}
, #{examYear}
, #{examGrpCd}
, #{examNo}
, #{subjectTypeCd}
, (SELECT IFNULL(MAX(SRT_NO), 0) + 1 FROM QUIZ_MSTR_INFO Y WHERE SUBJECT_TYPE_CD = #{subjectTypeCd})
, #{content}
, #{answer}
, #{cmntr}
, 'N'
, #{registId}
, #{registNm}
, CURRENT_TIMESTAMP
, CURRENT_TIMESTAMP
)

mapper.update:
UPDATE QUIZ_MSTR_INFO
SET SUBJECT_TYPE_CD = #{subjectTypeCd}
  , CONTENT         = #{content}
  , ANSWER          = #{answer}
  , CMNTR           = #{cmntr}
  , MDFY_TS         = CURRENT_TIMESTAMP
WHERE QUIZ_MSTR_INFO_SEQ = #{quizMstrInfoSeq}
