mapper.select:
SELECT ROW_NUMBER() OVER(PARTITION BY SUBJECT_TYPE_CD ORDER BY SUBJECT_TYPE_CD, SRT_NO) AS RNO , X.* FROM ( SELECT A.QUIZ_MSTR_INFO_SEQ , B.QUIZ_USER_ANS_SEQ , A.SUBJECT_TYPE_CD , A.SRT_NO , B.USER_ANSWER , ROW_NUMBER() OVER(PARTITION BY A.QUIZ_MSTR_INFO_SEQ) AS ERR_CHK FROM QUIZ_MSTR_INFO A LEFT JOIN ( SELECT QUIZ_MSTR_INFO_SEQ, QUIZ_USER_ANS_SEQ, USER_ANSWER FROM QUIZ_USER_ANS WHERE DEL_YN = 'N' AND USER_ID = #{userId}) B ON A.QUIZ_MSTR_INFO_SEQ = B.QUIZ_MSTR_INFO_SEQ WHERE 1 = 1 AND A.DEL_YN = 'N' AND A.EXAM_GRP_CD = #{examGrpCd} AND A.EXAM_YEAR = #{examYear} AND A.EXAM_NO = #{examNo}
AND A.SUBJECT_TYPE_CD = #{subjectTypeCd}
) X WHERE ERR_CHK = 1 ORDER BY SUBJECT_TYPE_CD, SRT_NO

SELECT COUNT(DISTINCT A.QUIZ_MSTR_INFO_SEQ)
FROM QUIZ_MSTR_INFO A,
     QUIZ_USER_ANS B
WHERE 1 = 1
  AND A.QUIZ_MSTR_INFO_SEQ = B.QUIZ_MSTR_INFO_SEQ
  AND A.DEL_YN = 'N'
  AND B.DEL_YN = 'N'
  AND A.EXAM_GRP_CD = #{examGrpCd}
  AND A.EXAM_YEAR = #{examYear}
  AND A.EXAM_NO = #{examNo}
  AND A.SUBJECT_TYPE_CD = #{subjectTypeCd}
  AND B.USER_ID = #{userId}
  AND (B.ANSWER_CHK IS NOT NULL AND B.USER_ANSWER != '')

mapper.insert:

    SELECT COALESCE(MAX(QUIZ_MSTR_INFO_SEQ), 0) + 1 FROM QUIZ_MSTR_INFO

INSERT INTO QUIZ_MSTR_INFO (
QUIZ_MSTR_INFO_SEQ
, EXAM_YEAR
, EXAM_GRP_CD
, EXAM_NO
, SUBJECT_TYPE_CD
, SRT_NO
, CONTENT
, ANSWER
, CMNTR
, DEL_YN
, REGIST_ID
, REGIST_NM
, REG_TS
, MDFY_TS
) VALUES (
#{quizMstrInfoSeq}
, #{examYear}
, #{examGrpCd}
, #{examNo}
, #{subjectTypeCd}
, (SELECT COALESCE(MAX(SRT_NO), 0) + 1 FROM QUIZ_MSTR_INFO Y WHERE SUBJECT_TYPE_CD = #{subjectTypeCd})
, #{content}
, #{answer}
, #{cmntr}
, 'N'
, #{registId}
, #{registNm}
, CURRENT_TIMESTAMP
, CURRENT_TIMESTAMP
)

mapper.update:
UPDATE QUIZ_MSTR_INFO
SET SUBJECT_TYPE_CD = #{subjectTypeCd}
  , CONTENT         = #{content}
  , ANSWER          = #{answer}
  , CMNTR           = #{cmntr}
  , MDFY_TS         = CURRENT_TIMESTAMP
WHERE QUIZ_MSTR_INFO_SEQ = #{quizMstrInfoSeq}

mapper.select:
SELECT QUIZ_MSTR_INFO_SEQ , EXAM_GRP_CD ,
, SUBJECT_TYPE_CD ,
, EXAM_YEAR , EXAM_NO , SRT_NO , CONTENT , ANSWER , CMNTR FROM QUIZ_MSTR_INFO WHERE QUIZ_MSTR_INFO_SEQ = #{quizMstrInfoSeq}
