mapper.insert:

    SELECT IFNULL(MAX(QUIZ_RESULT_INFO_SEQ), 0) + 1 AS QUIZ_RESULT_INFO_SEQ FROM QUIZ_RESULT_INFO

INSERT INTO QUIZ_RESULT_INFO (
    QUIZ_RESULT_INFO_SEQ
    , SUBJECT_TYPE_CD
    , EXAM_GRP_CD
    , EXAM_YEAR
    , EXAM_NO
    , USER_ID
    , RESULT_CNT
    , DEL_YN
    , REG_TS
    , MDFY_TS
)
VALUES (
    #{quizResultInfoSeq}
    , #{subjectTypeCd}
    , #{examGrpCd}
    , #{examYear}
    , #{examNo}
    , #{userId}
    , (SELECT COUNT(*) FROM (SELECT * FROM QUIZ_USER_ANS WHERE USER_ID = #{userId} AND ANSWER_CHK = 'O' AND DEL_YN = 'N') A, QUIZ_MSTR_INFO B
        WHERE A.QUIZ_MSTR_INFO_SEQ = B.QUIZ_MSTR_INFO_SEQ
           AND B.SUBJECT_TYPE_CD = #{subjectTypeCd} AND B.EXAM_GRP_CD = #{examGrpCd} AND EXAM_YEAR = #{examYear} AND EXAM_NO = #{examNo}
    )
    , 'N'
    , CURRENT_TIMESTAMP
    , CURRENT_TIMESTAMP
)

mapper.select:
SELECT RANK() OVER(ORDER BY RESULT_CNT DESC) AS RANK , USER_ID , NICKNAME , RESULT_CNT , REG_DT FROM ( SELECT (SELECT NICKNAME FROM USER_INFO WHERE USER_ID = A.USER_ID) AS NICKNAME , USER_ID , SUM(RESULT_CNT) AS RESULT_CNT , COUNT(*) AS CNT , DATE_FORMAT(REG_TS, '%Y-%m-%d') AS REG_DT FROM QUIZ_RESULT_INFO A WHERE DEL_YN = 'N' AND EXAM_GRP_CD = #{examGrpCd} AND EXAM_YEAR = #{examYear} AND EXAM_NO = #{examNo}
AND SUBJECT_TYPE_CD = #{subjectTypeCd}
GROUP BY USER_ID ) X
WHERE CNT = (SELECT COUNT(*) FROM CMMN_DTL_CD WHERE DEL_YN = 'N' AND USE_YN = 'Y' AND GRP_CD = '002')

    	SELECT COUNT(*) AS MEMBER_CNT
    	FROM (
	    	SELECT COUNT(*) AS QUIZ_CNT
	    	FROM QUIZ_RESULT_INFO
	        WHERE DEL_YN = 'N'
	            AND EXAM_GRP_CD = #{examGrpCd}
	            AND EXAM_YEAR = #{examYear}
	            AND EXAM_NO = #{examNo}
	        GROUP BY USER_ID
        ) X
        WHERE QUIZ_CNT = (SELECT COUNT(*) FROM CMMN_DTL_CD WHERE DEL_YN = 'N' AND USE_YN = 'Y' AND GRP_CD = '002')
