mapper.insert:

    SELECT COALESCE(MAX(QUIZ_RESULT_INFO_SEQ), 0) + 1 AS QUIZ_RESULT_INFO_SEQ FROM QUIZ_RESULT_INFO

INSERT INTO QUIZ_RESULT_INFO (
    QUIZ_RESULT_INFO_SEQ
    , SUBJECT_TYPE_CD
    , EXAM_GRP_CD
    , EXAM_YEAR
    , EXAM_NO
    , USER_ID
    , RESULT_CNT
    , DEL_YN
    , REG_TS
    , MDFY_TS
) VALUES (
    #{quizResultInfoSeq}
    , #{subjectTypeCd}
    , #{examGrpCd}
    , #{examYear}
    , #{examNo}
    , #{userId}
    , (SELECT COUNT(*) FROM (SELECT DISTINCT QUIZ_MSTR_INFO_SEQ FROM QUIZ_USER_ANS WHERE USER_ID = #{userId} AND ANSWER_CHK = 'O' AND DEL_YN = 'N') A, QUIZ_MSTR_INFO B
        WHERE A.QUIZ_MSTR_INFO_SEQ = B.QUIZ_MSTR_INFO_SEQ
           AND B.SUBJECT_TYPE_CD = #{subjectTypeCd} AND B.EXAM_GRP_CD = #{examGrpCd} AND EXAM_YEAR = #{examYear} AND EXAM_NO = #{examNo}
    )
    , 'N'
    , CURRENT_TIMESTAMP
    , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE QUIZ_RESULT_INFO SET
    RESULT_CNT = (SELECT COUNT(*) FROM (SELECT DISTINCT QUIZ_MSTR_INFO_SEQ FROM QUIZ_USER_ANS WHERE USER_ID = #{userId} AND ANSWER_CHK = 'O' AND DEL_YN = 'N') A, QUIZ_MSTR_INFO B
                  WHERE A.QUIZ_MSTR_INFO_SEQ = B.QUIZ_MSTR_INFO_SEQ
                    AND B.SUBJECT_TYPE_CD = #{subjectTypeCd} AND B.EXAM_GRP_CD = #{examGrpCd} AND EXAM_YEAR = #{examYear} AND EXAM_NO = #{examNo}
    )
    , MDFY_TS = CURRENT_TIMESTAMP
WHERE QUIZ_RESULT_INFO_SEQ = #{quizResultInfoSeq}

mapper.select:
SELECT QUIZ_RESULT_INFO_SEQ
FROM QUIZ_RESULT_INFO
WHERE DEL_YN = 'N'
    AND SUBJECT_TYPE_CD = #{subjectTypeCd}
    AND EXAM_GRP_CD = #{examGrpCd}
    AND EXAM_YEAR = #{examYear}
    AND EXAM_NO = #{examNo}
    AND USER_ID = #{userId}

SELECT RANK() OVER(ORDER BY RESULT_CNT DESC) AS RANK , USER_ID , NICKNAME , RESULT_CNT , REG_DT FROM ( SELECT (SELECT NICKNAME FROM USER_INFO WHERE USER_ID = A.USER_ID) AS NICKNAME , USER_ID , SUM(RESULT_CNT) AS RESULT_CNT , COUNT(*) AS CNT , TO_CHAR(REG_TS, 'YYYY-MM-DD') AS REG_DT FROM QUIZ_RESULT_INFO A WHERE DEL_YN = 'N' AND EXAM_GRP_CD = #{examGrpCd} AND EXAM_YEAR = #{examYear} AND EXAM_NO = #{examNo}
AND SUBJECT_TYPE_CD = #{subjectTypeCd}
GROUP BY USER_ID, REG_DT ORDER BY RESULT_CNT DESC ) X WHERE 1 = 1
LIMIT #{pageSize} OFFSET #{offset}

SELECT
    COUNT(*)
FROM (
    SELECT
        COUNT(*) AS CNT
         , USER_ID
    FROM QUIZ_RESULT_INFO A
    WHERE DEL_YN = 'N'
        AND EXAM_GRP_CD = #{examGrpCd}
        AND EXAM_YEAR = #{examYear}
        AND EXAM_NO = #{examNo}

        AND SUBJECT_TYPE_CD = #{subjectTypeCd}

    GROUP BY USER_ID
) X
WHERE 1 = 1

    	SELECT COUNT(*) AS MEMBER_CNT
    	FROM (
	    	SELECT COUNT(*) AS QUIZ_CNT
	    	FROM QUIZ_RESULT_INFO
	        WHERE DEL_YN = 'N'
	            AND EXAM_GRP_CD = #{examGrpCd}
	            AND EXAM_YEAR = #{examYear}
	            AND EXAM_NO = #{examNo}
	        GROUP BY USER_ID
        ) X
        WHERE X.QUIZ_CNT::INTEGER = (SELECT COUNT(*)::INTEGER FROM CMMN_DTL_CD WHERE DEL_YN = 'N' AND USE_YN = 'Y' AND GRP_CD = '002')
