#{quizUserAnsDtlSeq}
(SELECT QUIZ_USER_ANS_DTL_SEQ FROM (SELECT COALESCE(MAX(QUIZ_USER_ANS_DTL_SEQ), 0) + 1 AS QUIZ_USER_ANS_DTL_SEQ FROM QUIZ_USER_ANS_DTL) X )
, #{quizUserAnsSeq}
, (SELECT QUIZ_USER_ANS_SEQ FROM (SELECT COALESCE(MAX(QUIZ_USER_ANS_SEQ), 0) AS QUIZ_USER_ANS_SEQ FROM QUIZ_USER_ANS) X )
, #{quizDtlInfoSeq} , #{eraseYn} , 'SYSTEM' , 'SYSTEM' , CURRENT_TIMESTAMP , 'N' ) ON CONFLICT (QUIZ_USER_ANS_DTL_SEQ) DO UPDATE SET ERASE_YN = #{eraseYn} , DEL_YN = 'N' , MDFY_TS = CURRENT_TIMESTAMP


    SELECT COALESCE(MAX(QUIZ_USER_ANS_DTL_SEQ), 0) + 1 FROM QUIZ_USER_ANS_DTL

