INSERT INTO QUIZ_USER_ANS (
    QUIZ_USER_ANS_SEQ
    , QUIZ_MSTR_INFO_SEQ
    , USER_ID
    , USER_ANSWER
    , ANSWER_CHK
    , USER_CHK_IMPRT
    , USER_CHK_CNFSD
    , REGIST_ID
    , REGIST_NM
    , REG_TS
    , MDFY_TS
)
VALUES (


            #{quizUserAnsSeq}


            (SELECT QUIZ_USER_ANS_SEQ FROM (SELECT IFNULL(MAX(QUIZ_USER_ANS_SEQ), 0) + 1 AS QUIZ_USER_ANS_SEQ FROM QUIZ_USER_ANS) X )


    , #{quizMstrInfoSeq}
    , #{userId}
    , #{userAnswer}
    , CASE WHEN (SELECT #{userAnswer} = (SELECT ANSWER FROM QUIZ_MSTR_INFO WHERE QUIZ_MSTR_INFO_SEQ = #{quizMstrInfoSeq})) = '1' THEN 'O' ELSE 'X' END
    , #{userChkImprt}
    , #{userChkCnfsd}
    , 'SYSTEM'
    , 'SYSTEM'
    , CURRENT_TIMESTAMP
    , CURRENT_TIMESTAMP
) ON DUPLICATE KEY
UPDATE
    USER_ANSWER = #{userAnswer}
    , ANSWER_CHK = CASE WHEN (SELECT #{userAnswer} = (SELECT ANSWER FROM QUIZ_MSTR_INFO WHERE QUIZ_MSTR_INFO_SEQ = #{quizMstrInfoSeq})) = '1' THEN 'O' ELSE 'X' END
    , USER_CHK_IMPRT = #{userChkImprt}
    , USER_CHK_CNFSD = #{userChkCnfsd}
    , MDFY_TS = CURRENT_TIMESTAMP

