mapper.insert:

	SELECT IFNULL(MAX(USER_INFO_SEQ), 0) + 1 FROM USER_INFO

INSERT INTO USER_INFO (
	USER_INFO_SEQ
	, USER_ID
	, USER_NM
	, PASSWORD
	, EMAIL
	, ROLE
	, REG_TS
	, MDFY_TS
) VALUES (
	#{userInfoSeq}
	, #{userId}
	, #{userNm}
	, #{password}
	, #{email}
	, #{role}
	, current_timestamp()
	, current_timestamp()
)

mapper.update:
UPDATE USER_INFO SET
	MDFY_TS = current_timestamp()
WHERE USER_ID = #{userId}
