mapper.insert:

	SELECT IFNULL(MAX(USER_INFO_SEQ), 0) + 1 FROM USER_INFO

INSERT INTO USER_INFO (
	USER_INFO_SEQ
	, USER_ID
	, USER_NM
	, PASSWORD
	, EMAIL
	, REGIST_ID
	, REGIST_NM
	, REG_TS
	, MDFY_TS
) VALUES (
	#{userInfoSeq}
	, #{userId}
	, #{userNm}
	, #{password}
	, #{email}
	, 'SYSTEM'
	, 'SYSTEM'
	, current_timestamp()
	, current_timestamp()
)
