mapper.insert:

	SELECT IFNULL(MAX(USER_INFO_SEQ), 0) + 1 FROM USER_INFO

INSERT INTO USER_INFO (
	USER_INFO_SEQ
	, USER_ID
	, USER_NM
	, PASSWORD
	, NICKNAME
	, EMAIL
	, MOBILE
	, MEMBERSHIP_CD
	, ROLE
	, LOGIN_CNT
	, REGIST_ID
	, REGIST_NM
	, REG_TS
	, MDFY_TS
) VALUES (
	#{userInfoSeq}
	, #{userId}
	, #{userNm}
	, #{password}
	, #{nickname}
	, #{email}
	, #{mobile}
	, #{membershipCd}
	, #{role}
	, #{loginCnt}
	, 'SYETEM'
	, 'SYETEM'
	, current_timestamp()
	, current_timestamp()
)

mapper.update:
UPDATE USER_INFO SET
	MDFY_TS = current_timestamp()

		,EMAIL = #{email}


		, NICKNAME = #{nickname}

	, EXAM_TICKET = #{examTicket}
	, EXAM_TICKET_NO = #{examTicketNo}



WHERE USER_ID = #{userId}

UPDATE USER_INFO SET
	MEMBERSHIP_CD = #{membershipCd}
WHERE USER_ID = #{userId}
